function ninarow(array, n, item) {
  let found = false
  let accumulator = 0
  for (const token of array) {
    if (token === item) {
      accumulator += 1
    } else if (accumulator === n) {
      found = true
      break
    } else {
      accumulator = 0
    }
  }
  if (accumulator === n) {
    return true
  } else {
    return found
  }
}

module.exports = ninarow